INSERT INTO `home` (`id`, `type`, `name`, `image`, `content`, `created_at`) VALUES
(20, 0, 'Cesar Chavez', 'c4.jpg', '“Nếu bạn thực sự muốn kết bạn, hãy đi tới nhà ai đó và ngồi ăn với anh ta… Người mà cho bạn đồ ăn thì cũng cho bạn trái tim của họ.”\r\n								', '2016-12-07 01:49:31'),
(2, 1, 'Nora Ephron', 'g1.jpg', '“Tôi mắc phải rất nhiều sai lầm khi yêu, hối hận gần như tất cả các cuộc tình mình từng trải qua. Tuy nhiên, tôi không hề hối tiếc vì những món ăn ngon đi liền với chúng.”', '2016-11-07 15:26:48'),
(5, 0, 'Julia Child', 'c1.jpg', '“Người thích ăn uống luôn luôn là những người tốt nhất.”', '2016-11-07 15:29:57'),
(6, 0, 'Paul Prudhomme', 'c2.jpg', '“Bạn không cần 1 chiếc thìa bạc để thưởng thức đồ ăn ngon.”', '2016-11-07 15:30:22'),
(7, 0, 'Ernestine Ulmer', 'c3.jpg', '“Ai mà biết được đời sẽ ra sao. Nên là, hãy ăn món tráng miệng trước.”', '2016-11-07 15:30:46'),
(8, 0, 'John Gunther', 'c4.jpg', '“Mọi hạnh phúc trên đời đều xuất phát từ một bữa sáng nhàn nhã.”', '2016-11-07 15:31:07'),
(9, 1, 'M.F.K. Fisher', 'g7.jpg', '“Rượu và pho mát sinh ra là để dành cho nhau, cũng giống như cơn đau và thuốc giảm đau, tháng 6 và Mặt trăng, hay người tốt và những hành động cao quý.”', '2016-11-07 15:40:48'),
(10, 1, 'Orson Welles', 'g8.jpg', '“Bác sĩ của tôi nói rằng tôi cần ngừng tổ chức những bữa ăn thân mật cho 4 người – trừ khi có 3 người nữa tham dự.”', '2016-11-07 15:41:52'),
(12, 2, 'merry christmas 1', 'Merry-Christmas-wallpapers-2016.jpg', 'merry christmas 1', '2016-11-07 16:30:08'),
(13, 2, 'merry christmas 2', 'Free-Christmas-Pictures.jpg', 'merry christmas 2', '2016-11-07 16:31:00'),
(14, 2, 'merry christmas 3', 'maxresdefault.jpg', 'merry christmas 3', '2016-11-07 16:31:56'),
(21, 3, 'Ghé thăm nhà hàng ăn thử lẩu đặc biệt đồng giá 200.000 đồng', 'lau-129k-tai-confetti.jpg', '&nbsp;&nbsp;&nbsp;Chào đón mùa giáng sinh và năm mới sắp đến, nhằm gửi lời tri ân đến tất cả khách hàng, tại nhà hàng áp dụng chương trình khuyến mãi đặc biệt chỉ 200.000 đồng/ phần lẩu cho tất cả khách hàng, áp dụng từ 20/12/2016 cho đến hết ngày 20/01/2017. <br>\n&nbsp;&nbsp;&nbsp;Cụ thể, khách hàng khi đến với nhà hàng sẽ được tự do lựa chọn một trong các hương vị lẩu sau: Lẩu cá diêu hồng chua cay, lẩu kim chi hải sản, lẩu thái tôm yum kung, lẩu hoa quả hải sản, lẩu hải sản và sa tế, lẩu nấm hải sản chỉ với giá cực kỳ ưu đãi: 200.000 đồng/ phần lẩu (chưa bao gồm VAT và phí phục vụ). Ngoài ra, mỗi thực khách sẽ được tặng thêm 1 ly pepsi miễn phí.<br>\n&nbsp;&nbsp;&nbsp;Lẩu là món ăn có nguồn gốc từ Mông Cổ với phần nước được ninh từ xương hầm và củ cải đậm đà, tùy theo sở thích mùi vị mà người ta chế biến thành nhiều món lẩu khác nhau. Lẩu cá diêu hồng với vị chua ngọt đặc trưng, béo ngậy của cá và dậy mùi rau thơm. Lẩu kim chi hải sản se se cay với nước dùng đậm mùi kim chi và ngọt nước hải sản. Lẩu thái tôm yum yum chua cay đặc trưng hương vị của đất nước chùa vàng với rau thái và hải sản thơm ngon. Ngoài ra các món lẩu hải sản khác đều có chung thành phần chế biến chính là hải sản tươi sống thả vào nước dùng có hương vị đặc trưng và ăn kèm với rau nhúng rưới lên chút xíu bún tươi hay mì tươi được làm từ trứng gà. Đối với người Việt, nhắc đến lẩu chính là nhắc tới những khoảnh khắc được xuýt xoa thèm thuồng sum họp cùng gia đình hay bạn bè húp sùm sụp bát bún chan nước canh lẩu nóng hổi, chua ngọt, đậm đà vị tươi ngon của các món rau vùng nhiệt đới, của các món thịt hay hải sản được chế biến và ướp đậm đà. <br>\n&nbsp;&nbsp;&nbsp;Ngoài ra nơi đây còn được biết đến với nhiều tiện ích hấp dẫn như khu vực ghế massage dành cho khách, khu tiểu cảnh để khách hàng tha hồ chụp ảnh, khu vui chơi thú vị dành cho trẻ em, cửa hàng mua sắm và đặc biệt là điểm thưởng thức cà phê có view đẹp nhất trong sân bay - xứng đáng là điểm chờ và tiễn khách lý tưởng.<br>\n						\n								\n								\n								', '2016-11-14 02:37:12'),
(19, 4, 'Giới thiệu nhà hàng', 'noithat.png', 'Đến với nhà hàng Dũng Hải quý khách sẽ được tận hưởng một không gian mang đậm kiến trúc truyền thống nhưng không kém phần hiện đại – sang trọng. Cùng với phong cách phục vụ chuyên nghiệp nhà hàng sẽ đem đến cho khách hàng cảm giác thoải mái nhất.<br>\n							Nhà hàng : có hệ thống nhà hàng rộng lớn . Thiết bị tiện nghi hiện đại.<br>\n							- Nhà hàng phục vụ tiệc cưới hỏi , sinh nhật. Cùng lúc nhà hàng có khả năng đáp ứng nhu cầu của hàng trăm du khách. <br>\n							- Thực đơn phong phú với các món ăn Âu – Á có chất lượng , đặt biệt chú trọng các món ăn hợp khẩu vị của khách du lịch ba miền Bắc - Trung – Nam.   <br>\n							- Đội ngũ nhân viên phục vụ với phong cách chuyên nghiệp. Các đầu bếp tay nghề cao, nhiều năm kinh nghiệm. Hệ thống bàn ăn, nhà ăn được trang bị hiện đại, sạch sẽ, thoáng mát, giúp quý khách ngon miệng và rất đầm ấm. <br>\n							- Nhà hàng có dịch vụ đặt bàn trực tuyến và tạo thực đơn theo yêu cầu  của khách hàng. Quý khách có thể thanh toán trực tuyến cũng như tận hưởng mọi sự tiện lợi của nhà hàng mang lại.<br>\n							- Ngoài ra quý khách còn có thể đăng ký thành viên của nhà hàng để có thế nhận được nhiều ưu đãi, các chương trình khuyến mại theo từng chủ đề mà nhà hàng mang lại. <br>', '2016-11-14 02:21:38');
